/**
 * Sendbird JavaScript SDK wrapper
 * This file loads the Sendbird JavaScript SDK from the official CDN
 */

// Create a script element to load the Sendbird SDK
(function() {
    // Create a script element
    var script = document.createElement('script');

    // Set the source to the official Sendbird SDK CDN
    script.src = 'https://cdn.jsdelivr.net/npm/@sendbird/chat@4.10.7/dist/sendbird.min.js';

    // Set other attributes
    script.async = true;
    script.crossOrigin = 'anonymous';

    // Append the script to the document head
    document.head.appendChild(script);

    // Log that the script has been loaded
    console.log('Sendbird SDK script tag added to the document');

    // Add an event listener to handle script load errors
    script.onerror = function() {
        console.error('Failed to load Sendbird SDK from CDN');
    };

    // Add an event listener to handle script load success
    script.onload = function() {
        console.log('Sendbird SDK loaded successfully');

        // Define a global Sendbird object if it doesn't exist after loading
        if (typeof SendBird === 'undefined') {
            window.SendBird = window.Sendbird || {};
            console.log('Created fallback SendBird object');
        }
    };
})();